ruleToc.chapterList:
+@js:
doc=org.jsoup.Jsoup.parse(result);
html="";
if(result.match(/data-id="(\d+)"/)){
num=result.match(/查看隐藏章节\((\d+)\)/)[1];
p=parseInt(num/900);
for(var j=1;j<=p+1;j++){
bid=result.match(/data-bid="(\d+)"/)[1];
page=j;
url="https://www.zhaishuyuan.com/api/";
body="action=list&bid="+bid+"&page="+page;
option={
"method":"POST",
"body":String(body)
}
json=JSON.parse(java.ajax(url+","+JSON.stringify(option))).data;
for(var i=0; i<json.length; i++){ 				
html += '<li><a href="/chapter/'+bid+'/'+(json[i].id - bid)+'" target="_blank">'+json[i].cN+'</a> '+json[i].uT+'</li>';
}
}
doc.select("#more-chapter").before(html).remove();}
